(21, 3, '2023-01-13 18:02:28', '2023-01-13 18:02:28', '<!-- wp:paragraph -->\n<p>This test post was generated using the block theme Emptytheme in WordPress 6.1.1.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Image block:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image {\"id\":616,\"sizeSlug\":\"full\",\"linkDestination\":\"none\"} -->\n<figure class=\"wp-block-image size-full\"><img src=\"http://localhost/torikul/wp-content/uploads/2008/06/dsc20050727_091048_222.jpg\" alt=\"dsc20050727_091048_222\" class=\"wp-image-616\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph -->\n<p>Gallery:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:gallery {\"linkTo\":\"none\"} -->\n<figure class=\"wp-block-gallery has-nested-images columns-default is-cropped\"><!-- wp:image {\"id\":755,\"sizeSlug\":\"large\",\"linkDestination\":\"none\"} -->\n<figure class=\"wp-block-image size-large\"><img src=\"http://localhost/torikul/wp-content/uploads/2008/06/100_5540.jpg\" alt=\"Golden Gate Bridge\" class=\"wp-image-755\"/><figcaption class=\"wp-element-caption\">Golden Gate Bridge</figcaption></figure>\n<!-- /wp:image -->\n\n<!-- wp:image {\"id\":770,\"sizeSlug\":\"full\",\"linkDestination\":\"none\"} -->\n<figure class=\"wp-block-image size-full\"><img src=\"http://localhost/torikul/wp-content/uploads/2008/06/img_0767.jpg\" alt=\"Huatulco Coastline\" class=\"wp-image-770\"/><figcaption class=\"wp-element-caption\">Coastline in Huatulco, Oaxaca, Mexico</figcaption></figure>\n<!-- /wp:image -->\n\n<!-- wp:image {\"id\":760,\"sizeSlug\":\"large\",\"linkDestination\":\"none\"} -->\n<figure class=\"wp-block-image size-large\"><img src=\"http://localhost/torikul/wp-content/uploads/2008/06/dsc09114.jpg\" alt=\"Sydney Harbor Bridge\" class=\"wp-image-760\"/><figcaption class=\"wp-element-caption\">Sydney Harbor Bridge</figcaption></figure>\n<!-- /wp:image -->\n\n<!-- wp:image {\"id\":757,\"sizeSlug\":\"large\",\"linkDestination\":\"none\"} -->\n<figure class=\"wp-block-image size-large\"><img s